// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";

contract RewardContract is Ownable, ReentrancyGuard {
    IERC20 private g_token;
    IERC1155 private g_diamond;
    uint256 private g_diamondCounter;
    uint256 public g_locktime;
    uint256 public g_rewardsFunds;

    struct UserInfo {
        uint256 totalEarned;
        uint256 lastTime;
    }
    mapping (uint256 => uint256) public g_rewardsPerDay;
    mapping (uint256 => uint256) public g_minimumBalance;
    mapping (address => UserInfo) public g_userInfo;

    constructor(address _token, address _diamond) {
        g_token = IERC20(_token);
        g_diamond = IERC1155(_diamond);
        g_diamondCounter = 6;
        g_locktime = 86400; // 1day = 86400secs
    }

    function claimRewards() external nonReentrant {
        if(g_userInfo[msg.sender].lastTime == 0){
            g_userInfo[msg.sender].lastTime = block.timestamp;
            uint256 rewardsAmountInit = 0;
            for(uint i = 1; i < g_diamondCounter + 1; i++)
            {
                uint256 count = g_diamond.balanceOf(msg.sender, i);
                rewardsAmountInit += count * g_rewardsPerDay[i];
            }
            g_rewardsFunds -= rewardsAmountInit;
            g_token.transfer(msg.sender, rewardsAmountInit);
            g_userInfo[msg.sender].totalEarned = rewardsAmountInit;
            return;
        }

        require(block.timestamp - g_userInfo[msg.sender].lastTime >= g_locktime, "You can't claim rewards because time is not yet");
        uint256 rewardsAmount = 0;
        for(uint i = 1; i < g_diamondCounter + 1; i++)
        {
            uint256 count = g_diamond.balanceOf(msg.sender, i);
            rewardsAmount += count * g_rewardsPerDay[i];
        }
        require(rewardsAmount != 0 , "No Reward");
        require (g_rewardsFunds >= rewardsAmount, "Not enough funds");
        g_rewardsFunds -= rewardsAmount;
        g_token.transfer(msg.sender, rewardsAmount);
        g_userInfo[msg.sender].lastTime = block.timestamp;
        g_userInfo[msg.sender].totalEarned += rewardsAmount;
    }


    function getMinimumAmount() external view returns (uint256) {
        uint256 minBalance = 0;
        for(uint i = 1; i < g_diamondCounter + 1; i++)
        {
            uint256 count = g_diamond.balanceOf(msg.sender, i);
            minBalance += g_minimumBalance[i] * count;
        }
        return minBalance;
    }

    function getTotalReward(address _add) external view returns (uint256) {
        return g_userInfo[_add].totalEarned;
    }

    function getLastTime(address _add) external view returns (uint256) {
        return g_userInfo[_add].lastTime;
    }

    function getRewardsPerDay(uint256 _type) external view returns(uint256) {
        return g_rewardsPerDay[_type];
    }

    function setMinimumBalance(uint256 _type, uint256 _balance) external onlyOwner {
        g_minimumBalance[_type] = _balance;
    }

    function getMinimumBalance(uint256 _type) external view returns(uint256) {
        return g_minimumBalance[_type];
    }

    function getCurrentTime(address _add) external view returns(bool) {
        if(g_userInfo[_add].lastTime ==0 ) {
            return true;
        } else {
            uint256 isEnable = block.timestamp - g_userInfo[_add].lastTime ;
            if(isEnable >= g_locktime) {
                return true;
            } else {
                return false;
            }
        }
    }
    function setRewardsPerDay(uint256 _type, uint256 _amount) external onlyOwner {
        g_rewardsPerDay[_type] = _amount;
    }

    function setLockTime(uint256 _time) external onlyOwner {
        g_locktime = _time;
    }
    function setTokenAddress(address _token) external onlyOwner {
        g_token = IERC20(_token);
    }

    function setDiamondAddress(address _address)external onlyOwner {
        g_diamond = IERC1155(_address);
    }

    function setCounter (uint256 _num)external onlyOwner {
        g_diamondCounter = _num;
    }

    function fundsRewards(uint256 _amount) external onlyOwner {
        require(_amount > 0, "No funds");
        g_token.transferFrom(msg.sender, address(this), _amount);
        g_rewardsFunds += _amount;
    }
    
    function withdrawRewards() external onlyOwner {
        uint256 balance = g_token.balanceOf(address(this));
        g_token.transfer(msg.sender, balance);
    }
}
